import React from 'react';
import './App.css';
import HelloWorld from './HelloWorld';
import Button from 'react-bootstrap/Button';
import { useState } from 'react';
import { Component } from 'react';

/* structures */

const user = {
    username: 'Testovichok',
    imageUrl: 'https://img.mobiuspace.com/image/aigc/3fcfc0bb01cbbdb5d39ade3ef35b8018_512_512.webp',
    imageSize: 250,
    name: 'Тест',
    last_name: 'Тестовый',
    gender: 1,
    city:'Москва',
    adress: 'Ул. Пушкина',
    build: 'дом Колотушкина'
}

const number = [1,2,3,4];
const list = [
  {
    title:'React',
    url:"https://react.org",
    author: "Test Testovich",
    count_like: 5,
    count_comments: 3,
    id: 1,
  },
  {
    title:'Redux',
    url:"https://redux.js.org",
    author: "Dan Abamov",
    count_like: 5,
    count_comments: 3,
    id: 1,
  },
];

const exponent = number.map(function (number) {
  return number * number;
});



function App(){
    console.log(exponent);
    return(
            <div className="App">
                <Profile/>
            </div>
        );
}

class CountButton extends Component{
    constructor(props)
    {
        super(props);
        this.state = {count: this.props.count};
        console.log(this.props.count);
    };

    resetCount = () =>{
        this.setState({count:0});
    };

    addCount = () =>{
        this.setState({count: this.state.count + 1});
    };

    remCount = () =>{
        this.setState({count: this.state.count - 1});
    };

    render(){
        return(
            <>
                Count:{this.state.count}
                <button onClick={this.resetCount}>Reset</button>
                <button onClick={this.addCount}>+</button>
                <button onClick={this.remCount}>-</button>
            </>
        );
    };
}

function Profile()
{
    return(
        <div className="profile">
            <h1>{user.name}</h1>
            <img
                src={user.imageUrl}
                alt={'Photo by ' + user.name}
                style={{
                    width: user.imageSize,
                    height: user.imageSize
                }}
            />
        <div class="col px-4 pb-4 pt-5">
              <div class="card">
                <div class="card-body">
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Full Name</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      {user.name + " " + user.last_name}
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Пол</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      {user.gender == 0 ? "Мужской" : "Женский"}
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Phone</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      (239) 816-9029
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Mobile</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      (320) 380-4539
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Address</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      {user.city + ", " + user.adress + ", " + user.build }
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-12">
                      <CountButton count={10} />
                    </div>
                  </div>
                </div>
              </div>
             </div>

        </div>
    );
}

function listTechnologies(){
  return(
    <div>
      <h1>What I know</h1>

      <ul>
        {list.map(function(item)
        {
          return <li>{item.title}</li>
        })}
      </ul>
    </div>
  )
}

export default App;
