function Profile()
{
    return(
        <div className="profile">
            <h1>{user.name}</h1>
            <img
                src={user.imageUrl}
                alt={'Photo by ' + user.name}
                style={{
                    width: user.imageSize,
                    height: user.imageSize
                }}
            />
        <div class="col px-4 pb-4 pt-5">
              <div class="card">
                <div class="card-body">
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Full Name</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      {user.name + " " + user.last_name}
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Пол</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      {user.gender == 0 ? "Мужской" : "Женский"}
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Phone</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      (239) 816-9029
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Mobile</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      (320) 380-4539
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-3">
                      <h6 class="mb-0">Address</h6>
                    </div>
                    <div class="col-sm-9 text-secondary">
                      {user.city + ", " + user.adress + ", " + user.build }
                    </div>
                  </div>
                  <hr/>
                  <div class="row">
                    <div class="col-sm-12">
                      <CountButton count={10} />
                    </div>
                  </div>
                </div>
              </div>
             </div>

        </div>
    );
}

export default App;
